package javaDay03;

public class OperEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		
		int y = ++x;
		
		System.out.println(x);
		System.out.println(y);
		
		//출력후 x값 증가
		System.out.println(x++);
		System.out.println(x);
		//x값 증가후 출력
		System.out.println(++x);
		
	}

}
